package com.a.myfirstclass.java;
public class WhileLoop {

	   public static void main(String args[]) {
	      int x = 5;

	      while( x <=10 ) {
	         System.out.print("value of x : " + x );
	         x++;
	         System.out.print("\n");
	      }
	   }
	}

